mapper.select:
SELECT COUNT (id)
FROM t_w_end_paper
WHERE id IN
(SELECT id
FROM t_w_end_paper
WHERE node_code = #{nodeCode}
AND SUBSTRING(unit_code,5,2)
IN (
SELECT unit_type_code
FROM t_w_use_water_unit_role
WHERE person_id = #{userId}
)

	and unit_code like '%${unitCode}%'


	and unit_name like '%${unitName}%'


	and executed = #{executed}


	and water_meter_code like '%${waterMeterCode}%'


	and create_time >= #{applyTimeStart}


	and create_time <= #{applyTimeEnd}

)

SELECT TOP ${size} t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_code,t1.unit_name, t1.water_meter_code,t1.paper_type, (SELECT dict_item_name FROM t_dict_item WHERE dict_id =(SELECT id FROM t_dict WHERE dict_code = 'changeType') AND dict_item_code = t1.paper_type )AS paper_type_name, CASE t1.data_sources WHEN '1' THEN '网上申报' WHEN '2' THEN '现场申报' END AS data_sources, t1.create_time,t1.creater_id,t1.creater_name,t1.plan_year,t1.change_quarter,t1.cur_year_plan, t1.first_water,t1.second_water,t1.add_number,t1.add_way,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.first_quarter,t1.second_quarter,t1.third_quarter,t1.fourth_quarter, CASE t1.confirmed WHEN '0' THEN '否' WHEN '1' THEN '是' END AS confirmed, confirm_time, CASE t1.audit_status WHEN '0' THEN '否' WHEN '1' THEN '是' END AS audit_status, CASE t1.executed WHEN '0' THEN '否' WHEN '1' THEN '是' END AS executed, t1.execute_time,t1.executor,t1.executor_id,t1.algorithm_rules,t1.result FROM(SELECT row_number () OVER ( ORDER BY [unit_code] ASC ,[id]) AS rownumber,* FROM t_w_end_paper WHERE id IN (SELECT id FROM t_w_end_paper WHERE node_code = #{nodeCode} AND SUBSTRING(unit_code,5,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and executed = #{executed}
and water_meter_code like '%${waterMeterCode}%'
and create_time >= #{applyTimeStart}
and create_time <= #{applyTimeEnd}
) )t1 WHERE rownumber > (#{current}-1)* #{size}

mapper.update:
update t_w_end_paper
first_water= #{firstWater}, second_water = #{secondWater}, add_way = #{addWay}, quarter = #{quarter}, add_number = #{addNumber}, cur_year_plan = #{addNumber}, algorithm_rules = #{algorithmRules}, audit_status = #{auditStatus},
where id = #{id}

update t_w_end_paper
add_number= #{endPaper.addNumber}, cur_year_plan= #{endPaper.curYearPlan}, first_quarter= #{endPaper.firstQuarter}, second_quarter= #{endPaper.secondQuarter}, third_quarter= #{endPaper.thirdQuarter}, fourth_quarter= #{endPaper.fourthQuarter}, confirmed = #{endPaper.confirmed}, confirm_time = #{endPaper.confirmTime},
where water_plan_WX_id = #{endPaper.waterPlanWXId}
