mapper.select:
SELECT COUNT (id)
FROM t_w_use_water_plan
WHERE id IN
(SELECT id
FROM t_w_use_water_plan
WHERE node_code = #{nodeCode}
AND SUBSTRING(unit_code,5,2)
IN (
SELECT unit_type_code
FROM t_w_use_water_unit_role
WHERE person_id = #{userId}
)

  and unit_code like '%${unitCode}%'


  and unit_name like '%${unitName}%'


  and water_meter_code like '%${waterMeterCode}%'


  and plan_year >= #{yearStart}


  and plan_year <= #{yearEnd}

)

SELECT TOP ${size} t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_name,t1.unit_code, t1.water_meter_code,t1.plan_year,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.cur_year_plan,t1.first_quarter,t1.second_quarter, t1.third_quarter,t1.fourth_quarter, '当前计划' AS plan_type,t1.update_time,t1.remarks,t1.printed FROM(SELECT row_number () OVER ( ORDER BY [unit_code] ASC ,[id]) AS rownumber, id,node_code,use_water_unit_id,unit_name,unit_code, water_meter_code,plan_year,minus_pay_status,balance_test, create_type,cur_year_plan,first_quarter,second_quarter, third_quarter,fourth_quarter, COALESCE (update_time,create_time) AS update_time,remarks,printed FROM t_w_use_water_plan WHERE id IN (SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING(unit_code,5,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and plan_year >= #{yearStart}
and plan_year <= #{yearEnd}
) )t1 WHERE rownumber > (#{current}-1)* #{size}

SELECT t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_name,t1.unit_code, t1.water_meter_code,t1.plan_year,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.cur_year_plan,t1.first_quarter,t1.second_quarter, t1.third_quarter,t1.fourth_quarter, '当前计划' AS plan_type,COALESCE (t1.update_time,t1.create_time) AS update_time,t1.remarks,t1.printed FROM t_w_use_water_plan t1 WHERE id IN (SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING(unit_code,5,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and plan_year >= #{yearStart}
and plan_year <= #{yearEnd}
) ORDER BY t1.unit_code ASC

SELECT t1.use_water_unit_id,t1.unit_code,t1.unit_name,t1.water_meter_code,t1.plan_year,
t1.first_quarter,t1.second_quarter,t1.third_quarter,t1.fourth_quarter,
COALESCE((SELECT sum(COALESCE(water_number,0))
         FROM t_w_water_use_data
         WHERE use_month=#{thisMonth}
         AND   use_year =#{thisMonthYear}
         AND   use_water_unit_id =t1.use_water_unit_id
         GROUP BY use_water_unit_id
       ),0) AS first_water,
COALESCE((SELECT sum(COALESCE(water_number,0))
         FROM t_w_water_use_data
         WHERE use_month=#{monthBefore}
         AND   use_year =#{monthBeforeYear}
         AND   use_water_unit_id =t1.use_water_unit_id
         GROUP BY use_water_unit_id
      ),0) AS second_water
FROM t_w_use_water_plan t1
WHERE id IN
 ( SELECT id
   FROM t_w_use_water_plan
   WHERE node_code = #{nodeCode}
   AND SUBSTRING(unit_code,5,2) 
   IN (
       SELECT unit_type_code
       FROM t_w_use_water_unit_role
       WHERE person_id = #{userId}
  )
)
AND plan_year = #{thisMonthYear}
AND (exist_settlement_form ='0' or exist_settlement_form ='' or exist_settlement_form is NULL) 

  and unit_code like '%${unitCode}%'

mapper.update:
UPDATE t_w_use_water_plan
SET remarks = #{remarks}
WHERE id = #{id}

UPDATE t_w_use_water_plan
SET  printed= '1'
WHERE id = #{id}

UPDATE t_w_use_water_plan
SET  exist_settlement_form = #{existSettlement}
WHERE  unit_code = #{unitCode}
AND node_code = #{nodeCode}
AND plan_year =#{planYear}
