mapper.select:
select id, node_code, use_water_unit_id, unit_code, unit_name, water_meter_code, create_time, drawer, quarter, year, money, reason, treatment_advice, actual_amount, case t1.type when'1' then '退款单' when '2' then '减免单' else '未知' end as typeName from t_w_refund_or_refund t1 where node_code=#{nodeCode}
and id in(select busIness_id as id from t_sms_audit_flow where audit_status='1' and operator_id=#{userId})
and id in(select busIness_id as id from t_sms_audit_flow where (audit_status='2' or audit_status='3') and operator_id=#{userId})
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and t1.year =#{year}
and t1.type =#{type}
and t1.create_time >= #{startTime}
and t1.create_time < #{endTime}

SELECT top ${size} * FROM ( SELECT row_number ( ) over ( ORDER BY create_time DESC ) AS rownumber, id, node_code, use_water_unit_id, unit_code, unit_name, water_meter_code, create_time, drawer, quarter, year, money, reason, treatment_advice, actual_amount, case t1.type when'1' then '退款单' when '2' then '减免单' else '未知' end as typeName,
case when status='0' and ( SELECT count(1) FROM ( SELECT top 1 flow_node_role_id FROM t_flow_node WHERE flow_id = ( SELECT id FROM t_flow WHERE node_code =#{nodeCode} AND flow_code =#{flowCode}) ORDER BY flow_sort ASC ) t1 inner join (select user_id ,role_id from t_user_role_ref ) t4 on t4.role_id=t1.flow_node_role_id where user_id=#{userId} )>0 then '1' else '0' end as editBtn from t_w_refund_or_refund t1 where node_code=#{nodeCode}
and id in(select busIness_id as id from t_sms_audit_flow where audit_status='1' and operator_id=#{userId})
and id not in(select busIness_id as id from t_sms_audit_flow where audit_status='1' and operator_id=#{userId})
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and t1.year =#{year}
and t1.type =#{type}
and t1.create_time >= #{startTime}
and t1.create_time < #{endTime}
) temp_row WHERE rownumber > ( ( #{current}- 1 ) * #{size} );

select
count(1)
from t_w_refund_or_refund t1
where node_code=#{nodeCode}


	and id in(select busIness_id as id  from t_sms_audit_flow where audit_status='1' and operator_id=#{userId})



	and id not in(select busIness_id as id  from t_sms_audit_flow where audit_status='1' and operator_id=#{userId})


	and unit_code like '%${unitCode}%'


	and unit_name like '%${unitName}%'


	and water_meter_code like '%${waterMeterCode}%'


	and t1.year =#{year}


	and t1.type =#{type}


	and t1.create_time >= #{startTime}


	and t1.create_time < #{endTime}
