mapper.select:
SELECT TOP ${pageSize} c.* FROM ( SELECT row_number () OVER ( ORDER BY t1.unit_code DESC ) AS rownumber, t1.id, t1.node_code, t1.use_water_unit_id, t1.unit_name, t1.unit_code, t1.water_meter_code, t1.plan_year, t1.cur_year_plan, t1.first_quarter, t1.second_quarter, t1.third_quarter, t1.fourth_quarter, t1.first_water, t1.second_water, t1.plan_type,--调整类型 t1.change_quarter, t1.create_time,--创建时间 t1.creater_id, t1.audit_file_id, t1.water_proof_file_id, t1.other_file_id, t1.printed,--是否打印 t1.status, t1.remarks, t2.audit_status,--是否执行 t2.executed ,--是否审核 t2.confirm_time -- 审核时间 FROM t_w_use_water_plan_add t1 LEFT JOIN t_w_end_paper t2 ON t1.use_water_unit_id = t2.use_water_unit_id WHERE ( t1.node_code = #{nodeCode} AND t2.node_code = #{nodeCode} ) AND ( t1.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )) AND t2.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )))
and t1.unit_name like '%${unitName}%'
and SUBSTRING(t1.unit_code,3,2) like '%${userType}%'
and t2.executed = #{executed}
and t2.audit_status = #{auditStatus}
) c WHERE rownumber > (${pageSize}*(${currPage}-1))

SELECT count(1) FROM t_w_use_water_plan_add t1 LEFT JOIN t_w_end_paper t2 ON t1.use_water_unit_id = t2.use_water_unit_id WHERE ( t1.node_code = #{nodeCode} AND t2.node_code = #{nodeCode} ) AND ( t1.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )) AND t2.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )))
and t1.unit_name like '%${unitName}%'
and SUBSTRING(t1.unit_code,3,2) like '%${userType}%'
and t2.executed = #{executed}
and t2.audit_status = #{auditStatus}

SELECT TOP ${size} t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_name,t1.unit_code, t1.water_meter_code,t1.plan_year,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.cur_year_plan,t1.first_quarter,t1.second_quarter, t1.third_quarter,t1.fourth_quarter, '当前计划' AS plan_type,t1.update_time, '' AS printed,t1.remarks FROM(SELECT row_number () OVER ( ORDER BY [unit_code] ASC ,[id]) AS rownumber, id,node_code,use_water_unit_id,unit_name,unit_code, water_meter_code,plan_year,minus_pay_status,balance_test, create_type,cur_year_plan,first_quarter,second_quarter, third_quarter,fourth_quarter, COALESCE (update_time,create_time) AS update_time,remarks FROM t_w_use_water_plan WHERE id IN (SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING(unit_code,3,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} )
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and plan_year > #{yearStart}
and plan_year < #{yearEnd}
) )t1 WHERE rownumber > (#{current}-1)* #{size}

mapper.update:
UPDATE t_w_use_water_plan_add
SET remarks = #{remarks}
WHERE id = #{id}

update.foreach:
update t_w_use_water_plan_add
first_quarter = #{item.firstQuarter}, second_quarter = #{item.secondQuarter}, third_quarter = #{item.thirdQuarter}, fourth_quarter = #{item.fourthQuarter},
where id = #{item.id}

mapper.update:
UPDATE t_w_use_water_plan_add
SET

  status = #{status}


  printed = #{printed}

WHERE id = #{id}
