mapper.select:
SELECT TOP ${pageSize} c.* FROM ( SELECT row_number () OVER ( ORDER BY t1.unit_code DESC ) AS rownumber, id, node_code, use_water_unit_id, unit_name, unit_code, water_meter_code, plan_year, cur_year_plan, first_quarter, second_quarter, third_quarter, fourth_quarter, first_water, second_water, change_type, change_quarter, create_time, confirmed, confirm_time, audit_time, audit_person, audit_person_id, audit_result, audit_file_id, water_proof_file_id, other_file_id, check_adjust_water, first_quarter_quota, second_quarter_quota, third_quarter_quota, fourth_quarter_quota, (select t2.plan_year FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontPlanYear, --当前年度 (select t2.first_quarter FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontFirstQuarter, --当前第一季度 (select t2.second_quarter FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontSecondQuarter, --当前第二季度 (select t2.third_quarter FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontThirdQuarter, --当前第三季度 (select t2.fourth_quarter FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontFourthQuarter, --当前第四季度 (select t2.cur_year_plan FROM t_w_use_water_plan t2 WHERE t1.id = t2.id ) as FrontCurYearPlan, --当前年计划 CASE t1.audit_status WHEN '0' THEN '未执行' WHEN '1' THEN '已执行' ELSE '已执行' END audit_status, CASE t1.executed WHEN '0' THEN '未审核' WHEN '1' THEN '已审核' WHEN '2' THEN '已审核' ELSE '已审核' END executed, CASE t1.printed WHEN '0' THEN '未打印' WHEN '1' THEN '已打印' ELSE '已打印' END printed --是否打印
FROM t_wx_use_water_plan_add t1 WHERE t1.node_code = #{nodeCode} AND t1.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} ))
and t1.unit_name like '%${unitName}%'
and SUBSTRING(t1.unit_code,3,2) like '%${userType}%'
and t1.executed = #{executed}
and t1.audit_status = #{auditStatus}
) c WHERE rownumber > (${pageSize}*(${currPage}-1))

SELECT count(1) FROM t_wx_use_water_plan_add t1 WHERE t1.node_code = #{nodeCode} AND t1.id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} ))
and t1.unit_name like '%${unitName}%'
and SUBSTRING(t1.unit_code,3,2) like '%${userType}%'
and t1.executed = #{executed}
and t1.audit_status = #{auditStatus}

mapper.update:
update t_wx_use_water_plan_add set
  printed = '1'
WHERE id = #{id}
