mapper.select:
SELECT COUNT ( 1 ) FROM t_w_use_water_unit_invoice WHERE node_code = #{nodeCode}
AND invoice_number = #{invoiceNumber}
and deleted = '0'

SELECT COUNT ( 1 ) FROM t_w_use_water_unit_invoice WHERE 1 = 1
and id = #{id}
AND received = '1' and deleted = '0'

mapper.update:
UPDATE t_w_use_water_unit_invoice
SET person = #{personId}
WHERE
(convert(int,invoice_number) >= #{begin} AND convert(int,invoice_number) <= #{end})
AND person = #{loginId}
and deleted = '0'

mapper.select:
SELECT count(1) FROM t_w_use_water_unit_invoice t1 WHERE t1.deleted = '0' AND t1.person = #{loginId} AND t1.node_code = #{nodeCode}
and invoice_number like '%${invoiceNumber}%'
and (convert(int,invoice_number) >= #{begin} AND convert(int,invoice_number) <= #{end})
and enabled = #{enabled}
and received = #{received}
and id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{loginId} ))

SELECT TOP ${pageSize} c.* FROM ( SELECT row_number () OVER ( ORDER BY [invoice_number] DESC ) AS rownumber, id, pay_info_id, invoice_date, invoice_number, invoice_money, invoice_unit_name, enabled, drawer, create_time, invoice_type, invoice_unit_code, printed, person, received, receive_time, remarks, node_code, deleted FROM t_w_use_water_unit_invoice t1 WHERE t1.deleted = '0' AND t1.person = #{loginId} AND t1.node_code = #{nodeCode}
and invoice_number like '%${invoiceNumber}%'
and (convert(int,invoice_number) >= #{begin} AND convert(int,invoice_number) <= #{end})
and enabled = #{enabled}
and received = #{received}
and id IN ( SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING ( unit_code, 3, 2 ) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{loginId} )) ) c WHERE rownumber > (${pageSize}*(${currPage}-1)) ORDER BY invoice_number, invoice_unit_code DESC;

mapper.update:
update t_w_use_water_unit_invoice


    pay_info_id = #{payInfoId},


