mapper.select:
SELECT
max(
substring( unit_code, 7, 3 )
)
FROM
t_w_use_water_unit
where deleted='0'

  and unit_code like substring(#{unitCode},1,6)+'%'


  and id not in #{id}


  and node_code = #{nodeCode}

select *
from t_w_bank
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order By main desc

select *
from t_w_use_water_unit_contacts
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by main desc

select *
from t_file
where business_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by create_time desc

select *
from t_w_use_water_unit_modify
where node_code=#{nodeCode}
and use_water_unit_id=#{id}

select a.id , a.unit_code as unitCode, a.unit_name as unitName , STUFF(( SELECT ',' + water_meter_code FROM t_w_use_water_unit_meter WHERE use_water_unit_id=a.id FOR XML PATH('')), 1, 1, '' ) as waterMeterCode from t_w_use_water_unit as a where node_code=#{nodeCode} and a.unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}) and id in
#{item}
and id != #{notIn}

mapper.update:
update t_w_use_water_unit
set ${sql}
where id
in

  #{id}
