mapper.select:
SELECT
max(
substring( unit_code, 7, 3 )
)
FROM
t_w_use_water_unit
where deleted='0'

  and unit_code like substring(#{unitCode},1,6)+'%'


  and id not in #{id}


  and node_code = #{nodeCode}

SELECT top ${size} * FROM ( SELECT row_number ( ) over ( ORDER BY a.create_time desc
,a.unit_code ${unitCodeRank}
) AS rownumber, a.id, a.node_code, a.unit_code, a.unit_code_type, a.unit_code_group, STUFF(( SELECT ',' + water_meter_code FROM t_w_use_water_unit_meter WHERE use_water_unit_id=a.id and node_code=#{nodeCode} FOR XML PATH('')), 1, 1, '' ) as water_meter_code, a.responsibility_code, a.unit_name, a.industry, industryName=(select industry_name from t_w_quota_info where id=a.industry), b.contacts, b.mobile_number, b.phone_number, save_unit_type, unit_address, zip_name, zip_address, department, invoice_unit_name, area_country, gisX , gisY , imain, abnormal , abnormal_cause, case signed when '1' then '1' when '0' then '0' else '0'end as signed, bank_of_deposit, bank_account, responsibility_code as responsibilityCode, people_bank_pay_sys_number as peopleBankPaySysNumber, agreement_number as agreementNumber, entrust_unit_name as entrustUnitName, focus_user_remark, other_bank FROM t_w_use_water_unit as a
left join ( select use_water_unit_id,contacts,mobile_number,phone_number from t_w_use_water_unit_contacts where deleted='0' and node_code=#{nodeCode} and main='1' ) as b on a.id =b.use_water_unit_id
left join ( select use_water_unit_Id,bank_of_deposit,bank_account,signed,people_bank_pay_sys_number,agreement_number,entrust_unit_name,focus_user_remark,other_bank from t_w_bank where deleted='0' and main='1' ) as c on c.use_water_unit_Id=a.id WHERE a.unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}) and a.deleted='0' and a.node_code=#{nodeCode}
and a.id in (select use_water_unit_id as id from t_w_use_water_unit_meter where water_meter_code like '%${waterMeterCode}%' )
and a.id in (select use_water_unit_id as id from t_w_use_water_unit_contacts where mobile_number like '%${mobileNumber}%' and deleted='0')
and a.id in (select use_water_unit_Id as id from t_w_bank where bank_account like '%${bankAccount}%' and deleted='0')
and a.id in (select use_water_unit_Id as id from t_w_bank where signed =#{signed} and deleted='0') and signed=#{signed}
and a.abnormal =#{abnormal}
and a.unit_code like '%${unitCode}%'
and a.unit_name like '%${unitName}%'
and a.unit_code_type like '%${unitCodeType}%'
and a.responsibility_code like '%${responsibilityCode}%'
) temp_row WHERE rownumber > ( ( #{current}- 1 ) * #{size} );

select *
from t_w_bank
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order By main desc

select *
from t_w_use_water_unit_contacts
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by main desc

select *
from t_file
where business_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by create_time desc

select *
from t_w_use_water_unit_modify
where node_code=#{nodeCode}
and use_water_unit_id=#{id}

