mapper.update:
UPDATE t_w_use_water_unit_contacts SET deleted ='1',open_id= ''
WHERE id IN

  #{id}

mapper.select:
SELECT max( t.num ) AS num FROM ( SELECT count( 0 ) AS num FROM t_w_use_water_unit_contacts WHERE use_water_unit_id IN
#{id}
GROUP BY use_water_unit_id ) t

SELECT DISTINCT t1.id, t1.use_water_unit_id AS parentId, t1.node_code AS nodeCode, t2.unit_name AS orgName, 'person' AS tag, contacts AS userName, t1.mobile_number AS mobilePhone, t1.unit_code AS unitCode, '1' AS son FROM t_w_use_water_unit_contacts t1 INNER JOIN ( SELECT id,unit_name FROM t_w_use_water_unit WHERE deleted = '0'
and node_code = #{nodeCode}
AND SUBSTRING ( unit_code, 5, 2 ) in
#{father.id}
) t2 ON t1.use_water_unit_Id = t2.id order by parentId

SELECT contacts FROM t_w_use_water_unit_contacts WHERE deleted = '0' AND use_water_unit_id =
#{id}

  AND mobile_number != #{mobileNumber}


  and person_id != #{personId}

SELECT t1.id, t1.node_code as nodeCode, t1.unit_name as unitName, t1.unit_code as unitCode, SUBSTRING ( t1.unit_code, 5, 2 ) as unitType FROM t_w_use_water_unit t1 INNER JOIN ( SELECT unit_code FROM t_w_use_water_unit_contacts WHERE deleted = '0'
AND mobile_number = #{mobileNumber}
AND open_id = #{openId}
) t2 ON t1.unit_code = t2.unit_code WHERE t1.deleted = '0' order by t1.unit_code asc

SELECT id, node_code, unit_code, unit_name, unit_address, department FROM t_w_use_water_unit t1 where 1 = 1
and unit_code = #{unitCode}
and id IN ( SELECT use_water_unit_id FROM t_w_use_water_unit_contacts WHERE 1 = 1 and open_id = #{openId} AND deleted = '0' )
