mapper.update:
UPDATE t_w_use_water_unit_contacts SET deleted ='1',open_id= ''
WHERE id IN

  #{id}

mapper.select:
SELECT max( t.num ) AS num FROM ( SELECT count( 0 ) AS num FROM t_w_use_water_unit_contacts WHERE use_water_unit_id IN
#{id}
GROUP BY use_water_unit_id ) t

SELECT DISTINCT t1.id, t1.use_water_unit_id AS parentId, t1.node_code AS nodeCode, contacts AS orgName, 'person' AS tag, contacts AS userName, t1.mobile_number AS mobilePhone, t1.unit_code AS unitCode, '1' AS son FROM t_w_use_water_unit_contacts t1 INNER JOIN ( SELECT id FROM t_w_use_water_unit WHERE deleted = '0'
and node_code = #{nodeCode}
AND SUBSTRING ( unit_code, 5, 2 ) in
#{father.id}
) t2 ON t1.use_water_unit_Id = t2.id order by parentId
