mapper.select:
SELECT TOP ${size} t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_name,t1.unit_code, t1.water_meter_code,t1.plan_year,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.cur_year_plan,t1.first_quarter,t1.second_quarter, t1.third_quarter,t1.fourth_quarter, '当前计划' AS plan_type_name,t1.update_time,t1.remarks, CASE t1.printed WHEN '1' THEN '是' ELSE '否' END AS printed FROM(SELECT row_number () OVER ( ORDER BY [unit_code] ASC ,[id]) AS rownumber, id,node_code,use_water_unit_id,unit_name,unit_code, water_meter_code,plan_year,minus_pay_status,balance_test, create_type,cur_year_plan,first_quarter,second_quarter, third_quarter,fourth_quarter, COALESCE (update_time,create_time) AS update_time,remarks,printed FROM t_w_use_water_plan ta inner join (SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} GROUP BY unit_type_code) tb on SUBSTRING ( ta.unit_code, 5, 2 ) = tb.unit_type_code
inner JOIN (SELECT id AS unit_id FROM t_w_use_water_unit WHERE industry = #{industry})tc ON ta.use_water_unit_id=tc.unit_id
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and plan_year >= #{yearStart}
and plan_year <= #{yearEnd}
)t1 WHERE rownumber > (#{current}-1)* #{size}

mapper.update:
UPDATE t_w_use_water_plan
SET remarks = #{remarks}
WHERE id = #{id}

UPDATE t_w_use_water_plan
SET  printed= '1'
WHERE id = #{id}

UPDATE t_w_use_water_plan
SET  exist_settlement_form = #{existSettlement}
WHERE  unit_code = #{unitCode}
AND node_code = #{nodeCode}
AND plan_year =#{planYear}

mapper.select:
SELECT t1.id,t1.unit_code,t1.unit_name,t2.mobile_number,t2.contacts AS receiver_name FROM (SELECT id,use_water_unit_id,unit_code,unit_name FROM t_w_use_water_plan WHERE id IN (SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND plan_year =#{year} AND SUBSTRING(unit_code,5,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} UNION ALL SELECT 'NO_THIS_CODE' AS unit_type_code ) and unit_code like '%${unitCode}%'
and SUBSTRING(unit_code,3,2) = #{unitCodeGroup}
UNION ALL SELECT 'NO_THIS_ID' AS id ) )t1 LEFT JOIN ( SELECT use_water_unit_id,contacts,mobile_number FROM t_w_use_water_unit_contacts WHERE deleted = '0' )t2 ON t1.use_water_unit_id =t2.use_water_unit_id
