mapper.select:
SELECT * FROM t_w_quota_info WHERE deleted ='0'
AND id IN
#{id}

SELECT TOP  ${size} t1.*
FROM(SELECT row_number () OVER ( ORDER BY [create_time] DESC ,[id]) AS rownumber,*
FROM
t_w_quota_info
WHERE deleted = '0'
AND node_code = #{nodeCode}
AND parent_id is NULL 
AND parent_id = #{id}
AND industry_code LIKE '%${industryCode}%'
AND industry_name LIKE '%${industryName}%'
)t1
WHERE  rownumber > (#{current}-1)* #{size}

mapper.update:
UPDATE t_w_quota_info
SET deleted = '0'
,node_code = #{quotaInfo.nodeCode} 
,parent_id = #{quotaInfo.parentId} 
,industry_name = #{quotaInfo.industryName} 
,quota_unit = #{quotaInfo.quotaUnit} 
,quota_value = #{quotaInfo.quotaValue} 
,remark = #{quotaInfo.remark} 
,advance_value = #{quotaInfo.advanceValue}
,common_value = #{quotaInfo.commonValue}
,quota_rate = #{quotaInfo.quotaRate}
,time_param = #{quotaInfo.timeParam}
WHERE id = #{quotaInfo.id}
