mapper.select:
SELECT top ${json.pageSize} * FROM ( SELECT row_number ( ) over ( ORDER BY create_time DESC ) AS rownumber, id, node_code, use_water_unit_id, unit_code, unit_name, water_meter_code, create_time, drawer, quarter, year, money, reason, treatment_advice, actual_amount, status, case t1.type when'1' then '退款单' when '2' then '减免单' else '未知' end as typeName from t_w_refund_or_refund t1 where node_code=#{json.nodeCode} and is_revoke='0'
and id in(select busIness_id as id from
select #{item.businessId} as busIness_id, #{item.operatorId} as operator_id, #{item.auditStatus} as audit_status
t2 where audit_status='1' and operator_id=#{json.userId})
and id in( select busIness_id as id from
select #{item.businessId} as busIness_id, #{item.operatorId} as operator_id, #{item.auditStatus} as audit_status
t2 where (audit_status='2' or audit_status='3') and operator_id=#{json.userId})
and unit_code like '%${json.unitCode}%'
and unit_name like '%${json.unitName}%'
and water_meter_code like '%${json.waterMeterCode}%'
and t1.year =#{json.year}
and t1.type =#{json.type}
and t1.create_time >= #{json.startTime}
and t1.create_time < #{json.endTime}
) temp_row WHERE rownumber > ( ( #{json.current}- 1 ) * #{json.pageSize} );

SELECT count(1) from t_w_refund_or_refund t1 where node_code=#{json.nodeCode}
and id in(select busIness_id as id from
select #{item.businessId} as busIness_id, #{item.operatorId} as operator_id, #{item.auditStatus} as audit_status
t2 where audit_status='1' and operator_id=#{json.userId})
and id not in( select busIness_id as id from
select #{item.businessId} as busIness_id, #{item.operatorId} as operator_id, #{item.auditStatus} as audit_status
t2 where audit_status='1' and operator_id=#{json.userId})
and unit_code like '%${json.unitCode}%'
and unit_name like '%${json.unitName}%'
and water_meter_code like '%${json.waterMeterCode}%'
and t1.year =#{json.year}
and t1.type =#{json.type}
and t1.create_time >= #{json.startTime}
and t1.create_time < #{json.endTime}
;
