mapper.select:
SELECT top ${size} * FROM ( SELECT row_number ( ) over ( ORDER BY create_time DESC ) AS rownumber, id, node_code, use_water_unit_id, unit_code, unit_name, water_meter_code, create_time, drawer, quarter, year, money, reason, treatment_advice, actual_amount, status, case t1.type when'1' then '退款单' when '2' then '减免单' else '未知' end as typeName from t_w_refund_or_refund t1 where node_code=#{nodeCode}
and to_audit_person=#{userId}
and audit_persons like '%${userId}%'
and is_revoke='0'
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and t1.year =#{year}
and t1.type =#{type}
and t1.create_time >= #{startTime}
and t1.create_time < #{endTime}
) temp_row WHERE rownumber > ( ( #{current}- 1 ) * #{size} );

SELECT
count(1)
from t_w_refund_or_refund t1
where node_code=#{nodeCode}


  and to_audit_person=#{userId}


    and audit_persons like '%${userId}%'


and is_revoke='0'

  and unit_code like '%${unitCode}%'


  and unit_name like '%${unitName}%'


  and water_meter_code like '%${waterMeterCode}%'


  and t1.year =#{year}


  and t1.type =#{type}


  and t1.create_time >= #{startTime}


  and t1.create_time < #{endTime}

select count(1)
from t_w_refund_or_refund
where pay_id=#{payId}
and node_code=#{nodeCode}
and (status='0' or status='1')
and is_revoke='0'
