mapper.select:
SELECT TOP ${size} t1.id,t1.node_code,t1.use_water_unit_id,t1.unit_name,t1.unit_code, t1.water_meter_code,t1.plan_year,t1.minus_pay_status,t1.balance_test, t1.create_type,t1.cur_year_plan,t1.first_quarter,t1.second_quarter, t1.third_quarter,t1.fourth_quarter, '当前计划' AS plan_type,t1.update_time, '' AS printed,t1.remarks FROM(SELECT row_number () OVER ( ORDER BY [unit_code] ASC ,[id]) AS rownumber, id,node_code,use_water_unit_id,unit_name,unit_code, water_meter_code,plan_year,minus_pay_status,balance_test, create_type,cur_year_plan,first_quarter,second_quarter, third_quarter,fourth_quarter, COALESCE (update_time,create_time) AS update_time,remarks FROM t_w_use_water_plan WHERE id IN (SELECT id FROM t_w_use_water_plan WHERE node_code = #{nodeCode} AND SUBSTRING(unit_code,5,2) IN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{userId} UNION ALL SELECT 'NO_THIS_CODE' AS unit_type_code )
and unit_code like '%${unitCode}%'
and unit_name like '%${unitName}%'
and water_meter_code like '%${waterMeterCode}%'
and plan_year > #{yearStart}
and plan_year < #{yearEnd}
) )t1 WHERE rownumber > (#{current}-1)* #{size}

mapper.update:
UPDATE t_w_use_water_plan_add
SET remarks = #{remarks}
WHERE id = #{id}

update.foreach:
update t_w_use_water_plan_add
first_quarter = #{item.firstQuarter}, second_quarter = #{item.secondQuarter}, third_quarter = #{item.thirdQuarter}, fourth_quarter = #{item.fourthQuarter},
where id = #{item.id}

mapper.update:
UPDATE t_w_use_water_plan_add
SET

  status = #{status}


  printed = #{printed}

WHERE id = #{id}
