mapper.select:
SELECT
max(
substring( unit_code, 7, 3 )
)
FROM
t_w_use_water_unit
where deleted='0'

  and unit_code like substring(#{unitCode},1,6)+'%'


  and id not in #{id}


  and node_code = #{nodeCode}

SELECT top ${size} * FROM ( SELECT row_number ( ) over ( ORDER BY a.unit_code ) AS rownumber, a.id, a.node_code, a.unit_code, a.unit_code_type, a.unit_code_group, STUFF(( SELECT ',' + water_meter_code FROM t_w_use_water_unit_meter WHERE use_water_unit_id=a.id and node_code=#{nodeCode} FOR XML PATH('')), 1, 1, '' ) as water_meter_code, a.responsibility_code, a.unit_name, d.id as industry, d.industryName, b.contacts, b.mobile_number, b.phone_number, save_unit_type, unit_address, zip_name, zip_address, department, invoice_unit_name, area_country, gisX , gisY , imain, abnormal , abnormal_cause, remark, case signed when '1' then '1' when '0' then '0' else '0'end as signed, bank_of_deposit, bank_account, responsibility_code as responsibilityCode, people_bank_pay_sys_number as peopleBankPaySysNumber, agreement_number as agreementNumber, entrust_unit_name as entrustUnitName, focus_user_remark, other_bank FROM t_w_use_water_unit as a
left join ( select use_water_unit_id,contacts,mobile_number,phone_number from t_w_use_water_unit_contacts where deleted='0' and node_code=#{nodeCode} and main='1' ) as b on a.id =b.use_water_unit_id
left join ( select use_water_unit_Id,bank_of_deposit,bank_account,signed,people_bank_pay_sys_number,agreement_number,entrust_unit_name,focus_user_remark,other_bank from t_w_bank where deleted='0' and main='1' ) as c on c.use_water_unit_Id=a.id left join ( select id,industry_name as industryName from t_w_quota_info ) as d on d.id=a.industry WHERE a.unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}) and a.deleted='0' and a.node_code=#{nodeCode}
and a.id in (select use_water_unit_id as id from t_w_use_water_unit_meter where water_meter_code like '%${waterMeterCode}%' )
and a.id in (select use_water_unit_id as id from t_w_use_water_unit_contacts where mobile_number like '%${mobileNumber}%' and deleted='0')
and a.id in (select use_water_unit_id as id from t_w_use_water_unit_contacts where contacts like '%${contacts}%' and deleted='0')
and a.id in (select use_water_unit_Id as id from t_w_bank where bank_account like '%${bankAccount}%' and deleted='0')
and a.id in (select use_water_unit_Id as id from t_w_bank where signed =#{signed} and deleted='0')
and a.abnormal =#{abnormal}
and a.unit_code like '%${unitCode}%'
and a.unit_name like '%${unitName}%'
and a.unit_code_type like '%${unitCodeType}%'
and a.responsibility_code like '%${responsibilityCode}%'
and industry =#{industryId}
) temp_row WHERE rownumber > ( ( #{current}- 1 ) * #{size} );

select id,bank_of_deposit,bank_account,people_bank_pay_sys_number,agreement_number,entrust_unit_name,focus_user_remark,other_bank,main,signed
from t_w_bank
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order By main desc

select id,contacts,phone_number,mobile_number,main
from t_w_use_water_unit_contacts
where use_water_unit_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by main desc

select id,file_name,file_path,deleted
from t_file
where business_id =#{id}
and deleted='0'
and node_code=#{nodeCode}
order by create_time desc

select id,before_name,after_name,modify_time,modify_person
from t_w_use_water_unit_modify
where node_code=#{nodeCode}
and use_water_unit_id=#{id}
order by modify_time desc

select a.id , a.unit_code as unitCode, a.unit_name as unitName , a.imain, STUFF(( SELECT ',' + water_meter_code FROM t_w_use_water_unit_meter WHERE use_water_unit_id=a.id FOR XML PATH('')), 1, 1, '' ) as waterMeterCode from t_w_use_water_unit as a where node_code=#{nodeCode} and a.unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}) and id in
#{item}
and id != #{notIn}

mapper.update:
