mapper.select:
SELECT
max(
substring( unit_code, 7, 3 )
)
FROM
t_w_use_water_unit
where deleted='0'

  and unit_code like substring(#{unitCode},1,6)+'%'


  and id not in #{id}


  and node_code = #{nodeCode}

select
a.id ,
a.unit_code as unitCode,
a.unit_name as unitName ,
a.imain,
STUFF((
SELECT ',' + water_meter_code
FROM t_w_use_water_unit_meter
WHERE use_water_unit_id=a.id
FOR XML PATH('')), 1, 1, ''
) as waterMeterCode
from t_w_use_water_unit as a
inner join (
SELECT unit_type_code  FROM t_w_use_water_unit_role WHERE person_id =#{userId}
group by unit_type_code
) as b on a.unit_code_type=b.unit_type_code
where node_code=#{nodeCode}
and id in

  #{item}

and id != #{notIn}

mapper.update:
update t_w_use_water_unit
set ${sql}
where id
in

  #{id}

mapper.select:
select id, type='d', agreement_number as agreementNumber, sBankName=#{sBankName}, sBankNum=#{sBankNum}, sUnitName=#{sUnitName}, sBankAccount=#{sBankAccount}, bank_of_deposit as bankOfDeposit, people_bank_pay_sys_number as peopleBankPaySysNumber, entrust_unit_name as entrustUnitName, bank_account as bankAccount, case when isExport='0' then '未导出过' when isExport='1' then '已导出过' end as isExport from t_w_bank where deleted='0' and main='3'
and revocation_date >= #{startTime}
and revocation_date < #{endTime}

select a.id, a.unit_code as unitCode, a.unit_name as unitName, a.unit_address as unitAddress, a.area_country as areaCountry, [waterMeterCode] = STUFF(( SELECT ',' + [water_meter_code] from t_w_use_water_unit_meter WHERE use_water_unit_id = a.id FOR XML PATH ( '' )), 1, 1, '' ), a.responsibility_code as responsibilityCode, a.save_unit_type as saveUnitType,
case (select max(signed) as signed from t_w_bank where deleted='0' and use_water_unit_id=a.id ) when '1' then '1' when '0' then '0' else '0'end as signed, b.bank_of_deposit as bankOfDeposit, b.bank_account as bankAccount, a.zip_name as zipName, a.zip_address as zipAddress, a.invoice_unit_name as invoiceUnitName, a.unit_code_type as unitCodeType, a.department , a.abnormal_cause, c.industry_name as industryName, b.people_bank_pay_sys_number as peopleBankPaySysNumber, b.agreement_number as agreementNumber, b.entrust_unit_name as entrustUnitName, b.focus_user_remark as focusUserRemark, b.other_bank as otherBank from (select *from t_w_use_water_unit where deleted='0') as a left join ( SELECT use_water_unit_id,bank_of_deposit,bank_account,people_bank_pay_sys_number,agreement_number,entrust_unit_name,focus_user_remark,other_bank FROM t_w_bank WHERE deleted = '0' and main='1' ) b ON b.use_water_unit_id = a.id left join (select id,industry_name from t_w_quota_info where deleted='0') as c on c.id=a.industry where a.id in
#{id}

select id , unit_name as unitName, gisX, gisY from t_w_use_water_unit where deleted='0'
and node_code=#{nodeCode}
and create_time < #{year}
and unit_name like '%${unitName}%'

SELECT useWaterCount, isnull(yearPlan,0) as yearPlan, isnull(actualWater,0) as actualWater FROM
( select count(1) as useWaterCount from t_w_use_water_unit where deleted='0'
and node_code=#{nodeCode}
and create_time < #{createTime}
) n1,
( select yearPlan=sum(t2.first_quarter)+sum(t2.second_quarter)+sum (t2.third_quarter)+sum(t2.fourth_quarter) from ( select id from t_w_use_water_unit where deleted='0'
and node_code=#{nodeCode}
) t1
LEFT join ( SELECT * FROM t_w_use_water_plan WHERE plan_type = '0' and plan_year=#{year}) t2 ON t2.use_water_unit_id = t1.id ) n2,
( select sum (actualWater) as actualWater from ( select id from t_w_use_water_unit where deleted='0'
and node_code=#{nodeCode}
) t1 left join ( select use_water_unit_id, actualWater=sum(january_count)+sum(february_count)+sum(march_count)+sum(april_count)+sum(may_count)+sum(june_count)+sum(july_count)+sum(august_count)+sum(september_count)+sum(october_count)+sum(november_count)+sum(december_count) from t_w_water_month_use_data where use_year=#{year} group by use_water_unit_id )as t2 on t2.use_water_unit_id=t1.id ) n3

SELECT unit_code_type AS id, t5.node_code AS nodeCode, t5.unit_code_type AS orgName, 'org' AS tag, '1' AS son FROM ( SELECT DISTINCT unit_code_type, node_code FROM t_w_use_water_unit t3 INNER JOIN ( SELECT use_water_unit_Id FROM t_w_use_water_unit_contacts t1 INNER JOIN ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE 1 = 1
and person_id = #{userId}
and node_code = #{nodeCode}
) t2 ON SUBSTRING ( t1.unit_code, 5, 2 ) = t2.unit_type_code WHERE deleted = '0' ) t4 ON t3.id = t4.use_water_unit_Id WHERE deleted = '0'
and node_code = #{nodeCode}
) t5 WHERE 1 = 1
and unit_code_type like '%${condition}%'
ORDER BY t5.unit_code_type ASC

SELECT t3.id, t3.node_code AS nodeCode, t3.unit_code_type AS parentId, t3.unit_address AS address, t3.unit_name AS orgName, t3.create_time AS createTime, t3.remark, 'org' AS tag, CASE ( SELECT COUNT ( id ) FROM t_w_use_water_unit_contacts t2 WHERE deleted = '0' AND main = '1' AND t2.use_water_unit_id = t3.id ) WHEN 0 THEN '0' ELSE '1' END son FROM t_w_use_water_unit t3 where deleted = '0'
AND node_code = #{ nodeCode }
AND SUBSTRING ( t3.unit_code, 5, 2 ) in
#{father.id}
order by id

SELECT top ${size} id, unit_code as unitCode, unit_name as unitName FROM ( SELECT row_number ( ) over ( ORDER BY unit_code_type ,cast(SUBSTRING(unit_code,7,10) as INTEGER) ) AS rownumber, id, unit_code , unit_name from ( select id, unit_code, unit_name, unit_code_type from t_w_use_water_unit where deleted='0' and node_code=#{nodeCode}
and (unit_code like '%${keyword}%' or unit_name like '%${keyword}%')
)as t1 inner join ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id =#{userId} group by unit_type_code ) as t2 on t1.unit_code_type=t2.unit_type_code
) temp_row WHERE rownumber > ( ( #{current}- 1 ) * #{size} );

SELECT count(1) from ( select id, unit_code, unit_name, unit_code_type from t_w_use_water_unit where deleted='0' and node_code=#{nodeCode}
and (unit_code like '%${keyword}%' or unit_name like '%${keyword}%')
)as t1 inner join ( SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id =#{userId} group by unit_type_code ) as t2 on t1.unit_code_type=t2.unit_type_code
