mapper.select:
SELECT COUNT( 1 )
FROM
t_w_use_water_unit_role t1
WHERE 1 = 1

  and t1.person_id = #{personId}


  AND t1.unit_type_code = #{unitTypeCodes}


  AND t1.node_code = #{nodeCode}

SELECT unit_type_code
FROM
t_w_use_water_unit_role t1
WHERE 1 = 1

  and t1.person_id = #{personId}


  AND t1.node_code = #{nodeCode}

SELECT unit_type_code FROM t_w_use_water_unit_role WHERE person_id = #{personId}
AND node_code = #{nodeCode}
GROUP BY unit_type_code
