mapper.select:
SELECT id, use_water_unit_id, use_year, water_meter_code, january_count, february_count, march_count, april_count, may_count, june_count, july_count, august_count, september_count, october_count, november_count, december_count, now_price, node_code, is_warning, caliber, sector, water_use_kinds, unit_names, unit_addresss, ( january_count + february_count + march_count + april_count + may_count + june_count + july_count + august_count + september_count + october_count + october_count + november_count + december_count ) AS waterNumber FROM t_w_water_month_use_data WHERE node_code = #{nodeCode}
AND use_water_unit_id = #{useWaterUnitId}
AND use_year = DateName(YEAR,GetDate()) ORDER BY waterNumber DESC;

mapper.update:
update t_w_water_month_use_data set use_water_unit_id = null
WHERE id IN

  #{id}

mapper.select:
SELECT
top ${size}
*
FROM
(
select
row_number ( ) over ( ORDER BY use_year desc

  ,unit_code ${unitCodeRank}


  ,unit_name ${unitNameRank}

) AS rownumber,
*
from
(
SELECT
a.*,
b.unit_code,
( a.january_count + a.february_count + a.march_count + a.april_count
+ a.may_count + a.june_count + a.july_count + a.august_count + a.september_count
+ a.october_count + a.october_count + a.november_count + a.december_count ) AS waterNumber
FROM
t_w_water_month_use_data as a
left join (select id,unit_code  from t_w_use_water_unit) as b on a.use_water_unit_id=b.id
WHERE
a.node_code=#{nodeCode}
)t1
where 1=1


  and unit_code like '%${unitCode}%'



  and unit_names like '%${unitName}%'



  and water_meter_code like '%${waterMeterCode}%'



  and waterNumber >= #{waterNumberStart}



  and waterNumber <= #{waterNumberEnd}



  and now_price >= #{nowPriceStart}



  and now_price <= #{nowPriceEnd}



  and caliber >= #{caliberStart}



  and caliber <= #{caliberEnd}



  and sector >= #{sectorStart}



  and sector <= #{sectorEnd}



  and use_year = #{useYear}

) temp_row
WHERE
rownumber > ( ( #{current}- 1 ) * #{size} );

select
count(*)
from
(
SELECT
a.*,
b.unit_code,
( a.january_count + a.february_count + a.march_count + a.april_count
+ a.may_count + a.june_count + a.july_count + a.august_count + a.september_count
+ a.october_count + a.october_count + a.november_count + a.december_count ) AS waterNumber
FROM
t_w_water_month_use_data as a
left join t_w_use_water_unit as b on a.use_water_unit_id=b.id
WHERE
a.node_code=#{nodeCode}
) t1
where 1=1


  and unit_code like '%${unitCode}%'



  and unit_names like '%${unitName}%'



  and water_meter_code like '%${waterMeterCode}%'



  and waterNumber >= #{waterNumberStart}



  and waterNumber <= #{waterNumberEnd}



  and now_price >= #{nowPriceStart}



  and now_price <= #{nowPriceEnd}



  and caliber >= #{caliberStart}



  and caliber <= #{caliberEnd}



  and sector >= #{sectorStart}



  and sector <= #{sectorEnd}



  and use_year = #{useYear}

;

select
*
from
(
SELECT
a.*,
b.unit_code,
( a.january_count + a.february_count + a.march_count + a.april_count
+ a.may_count + a.june_count + a.july_count + a.august_count + a.september_count
+ a.october_count + a.october_count + a.november_count + a.december_count ) AS waterNumber
FROM
t_w_water_month_use_data as a
left join t_w_use_water_unit as b on a.use_water_unit_id=b.id
WHERE
a.node_code=#{nodeCode}
)t1
where 1=1


  and unit_code like '%${unitCode}%'



  and unit_names like '%${unitName}%'



  and water_meter_code like '%${waterMeterCode}%'



  and waterNumber >= #{waterNumberStart}



  and waterNumber <= #{waterNumberEnd}



  and now_price >= #{nowPriceStart}



  and now_price <= #{nowPriceEnd}



  and caliber >= #{caliberStart}



  and caliber <= #{caliberEnd}



  and sector >= #{sectorStart}



  and sector <= #{sectorEnd}



  and use_year = #{useYear}

SELECT TOP 1 id, use_water_unit_id, use_year, water_meter_code, january_count, february_count, march_count, april_count, may_count, june_count, july_count, august_count, september_count, october_count, november_count, december_count, now_price, node_code, is_warning, caliber, sector, water_use_kinds, unit_names, unit_address, ( SELECT SUM ( january_count + february_count + march_count + april_count + may_count + june_count + july_count + august_count + september_count + october_count + november_count + december_count ) FROM t_w_water_month_use_data t2 WHERE 1 = 1
and t2.water_meter_code = #{waterMeterCode}
AND t1.id = t2.id ) AS waterNumber FROM t_w_water_month_use_data t1 WHERE 1 = 1
and water_meter_code = #{waterMeterCode}
ORDER BY use_year desc
