mapper.select:
SELECT count(t1.id) as counts, sum(t1.amount_receivable) as amountReceivableSum, sum(t1.actual_amount) as actualAmountSum, sum( case t1.pay_status when '1' then 1 else 0 end ) as payStatusSum, sum( case t1.cash_check when '1' then 1 else 0 end ) as cashCheckSum, sum( case t1.transfer_check when '1' then 1 else 0 end ) as transferCheckSum FROM ( SELECT * FROM t_w_water_use_pay_info WHERE 1 = 1
and unit_name like '%${unitName}%'
and unit_code like '%${unitCode}%'
and count_year=#{countYear}
and count_quarter=#{countQuarter}
and (pay_status='1' or pay_status= '5' )
and pay_status not in ('1','2','3','5')
and actual_amount =#{actualAmount}
and edited_actual=#{editedActual}
and pay_type=#{payType}
) t1 inner join ( select id , [water_meter_code] = STUFF ( ( SELECT ',' + [water_meter_code] FROM t_w_use_water_unit_meter t WHERE t.use_water_unit_id = uu.id FOR XML PATH ( '' ) ),1,1,''), responsibility_code, unit_code_group from t_w_use_water_unit uu where deleted='0'
and unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId})
and unit_code_type=#{userType}
) t2 on t2.id=t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_bank WHERE main = '1' AND deleted = '0' ) t4 ON t4.use_water_unit_id = t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_use_water_base_plan WHERE deleted = '0' ) t5 ON t5.use_water_unit_id = t1.use_water_unit_id AND t5.plan_year = t1.count_year
where t2.water_meter_code like '%${waterMeterCode}%'

SELECT top ${size} temp_row.* FROM ( SELECT row_number ( ) over ( ORDER BY t1.unit_code, unit_code_group, count_quarter DESC ) AS rownumber, t1.id, t1.pay_type , t1.unit_code , t1.unit_name , t2.unit_code_group , t1.count_year , t1.count_quarter , t1.price, t1.year_plan, t1.quarter_plan , t1.water_num1 , t1.water_num2 , t1.water_num3 , t1.water_num_amount , t1.exceed_water , t1.pay_ratio , t1.multiple, t1.actual_multiple , t1.amount_receivable , t1.actual_amount , t1.increase_money , t1.pay_status ,
case t1.pay_status when '1' then 1 else 0 end as payStatusName, t1.entrusted, t1.cash_check , t1.transfer_check , t1.printed, t1.invoice_num , t1.invoice_print_time , t1.use_water_unit_id, t2.responsibility_code AS responsibilityCode, t2.water_meter_code as waterMeterCode, t4.other_bank AS otherBank, t4.bank_of_deposit as bankOfDeposit, t4.bank_account as bankAccount, t1.remarks,
case #{countQuarter} when '1' then t5.one_quarter when '2' then t5.two_quarter when '3' then t5.three_quarter when '4' then t5.four_quarter else '0' end as baseQuarterPlan,
case when t4.signed ='1' then '是' when t4.signed='0' then '否' else '否' end as signed FROM ( SELECT * FROM t_w_water_use_pay_info WHERE node_code = #{nodeCode}
and unit_name like '%${unitName}%'
and unit_code like '%${unitCode}%'
and count_year=#{countYear}
and count_quarter=#{countQuarter}
and (pay_status='1' or pay_status= '5' )
and pay_status not in ('1','2','3','5')
and actual_amount =#{actualAmount}
and edited_actual=#{editedActual}
and pay_type=#{payType}
) t1 inner join ( select id , [water_meter_code] = STUFF ( ( SELECT ',' + [water_meter_code] FROM t_w_use_water_unit_meter t WHERE t.use_water_unit_id = uu.id FOR XML PATH ( '' ) ),1,1,''), responsibility_code, unit_code_group from t_w_use_water_unit uu where deleted='0'
and unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId})
and unit_code_type=#{userType}
) t2 on t2.id=t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_bank WHERE deleted = '0' and main='1' ) t4 ON t4.use_water_unit_id = t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_use_water_base_plan WHERE deleted = '0' ) t5 ON t5.use_water_unit_id = t1.use_water_unit_id AND t5.plan_year = t1.count_year
where t2.water_meter_code like '%${waterMeterCode}%'
) temp_row WHERE rownumber > ( (#{current}-1)* #{size} );

mapper.delete:
delete from t_w_water_use_pay_info
where pay_status !=1
and pay_status !=5
and  edited_actual ='0'
and  (invoice_num is null or invoice_num='')

	and count_year=#{countYear}

and count_quarter=#{countQuarter}


	and use_water_unit_id in

		#{useWaterUnitId}

mapper.select:
select * from ( select t1.year, t1.quarter, case when t2.payStatus is null then '无加价' else t2.payStatus end payStatus from ( select year=#{year}, quarter from t_w_quarter_conf ) t1 left join ( select count_year as countYear, countQuarter=count_quarter, case when pay_status != 1 and pay_status != 5 THEN '未缴纳' else '已缴纳' end payStatus from t_w_water_use_pay_info where count_year=#{year} and use_water_unit_id=#{unitId} ) as t2 on t1.quarter=t2.countQuarter and t1.year=t2.countYear
) bb PIVOT( max(payStatus) FOR [quarter] IN ([1], [2], [3],[4]) ) a
UNION ALL select * from ( select t1.year, t1.quarter, case when t2.payStatus is null then '无加价' else t2.payStatus end payStatus from ( select year=#{year}-1, quarter from t_w_quarter_conf ) t1 left join ( select count_year as countYear, countQuarter=count_quarter, case when pay_status != 1 and pay_status != 5 THEN '未缴纳' else '已缴纳' end payStatus from t_w_water_use_pay_info where count_year=#{year}-1 and use_water_unit_id=#{unitId} ) as t2 on t1.quarter=t2.countQuarter and t1.year=t2.countYear
) bb PIVOT( max(payStatus) FOR [quarter] IN ([1], [2], [3],[4]) ) a
UNION
select * from ( select t1.year, t1.quarter, case when t2.payStatus is null then '无加价' else t2.payStatus end payStatus from ( select year=#{year}-2, quarter from t_w_quarter_conf ) t1 left join ( select count_year as countYear, countQuarter=count_quarter, case when pay_status != 1 and pay_status != 5 THEN '未缴纳' else '已缴纳' end payStatus from t_w_water_use_pay_info where count_year=#{year}-2 and use_water_unit_id=#{unitId} ) as t2 on t1.quarter=t2.countQuarter and t1.year=t2.countYear
) bb PIVOT( max(payStatus) FOR [quarter] IN ([1], [2], [3],[4]) ) a order by year ;

select unit_name as unitName, price as price, year_plan as yearPlan, quarter_plan as quarterPlan, water_num1 as waterNum1, water_num2 as waterNum2, water_num3 as waterNum3, water_num_amount as waterNumAmount, exceed_water as exceedWater, pay_ratio as payRatio, multiple, actual_multiple AS actualMultiple, amount_receivable AS amountReceivable, actual_amount AS actualAmount,
case pay_status when '1' then '已缴费' when '5' then '已缴费' else '未缴费' end as payStatus, entrusted, t4.other_bank AS otherBank, t4.bank_of_deposit as bankOfDeposit, t4.bank_account as bankAccount,
case when t4.signed is null then '0' else '1' end signed from t_w_water_use_pay_info as t1 LEFT JOIN ( SELECT * FROM t_w_bank WHERE deleted = '0' and signed='1') t4 ON t4.use_water_unit_id = t1.use_water_unit_id where count_year=#{year} and count_quarter=#{quarter}

select id, unit_code as unitCode, unit_name as unitName, unit_address as unitAddress, area_country as areaCountry, responsibility_code as responsibilityCode, case when save_unit_type='1' then '是' when save_unit_type is null then '否' when save_unit_type='0' then '否' else '否' end as saveUnitType, case when signed='1' then '是' when signed is null then '否' when signed='0' then '否' else '否' end as signed, bank_of_deposit as bankOfDeposit, bank_account as bankAccount, zip_address as zipAddress, zip_name as zipName, invoice_unit_name as invoiceUnitName, unit_code_type as userType, department, industry_name=(select industry_name from t_w_quota_info where id=t2.industry), people_bank_pay_sys_number as peopleBankPaySysNumber, agreement_number as agreementNumber, focus_user_remark as focusUserRemark, other_bank as otherBank, abnormal_cause as abnormalCause, STUFF(( SELECT ',' + [water_meter_code] FROM t_w_use_water_unit_meter WHERE use_water_unit_id=t2.id FOR XML PATH('')), 1, 1, '' ) as waterMeterCode from ( select use_water_unit_id from t_w_water_use_pay_info where 1=1
and count_year=#{year}
and count_quarter=#{quarter}
) t1 inner join (select id,unit_code,unit_name,unit_address,area_country,responsibility_code,save_unit_type,zip_address,zip_name,invoice_unit_name,unit_code_type,industry,department,abnormal_cause from t_w_use_water_unit where deleted='0' and unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}))t2 on t2.id=t1.use_water_unit_id LEFT JOIN ( SELECT use_water_unit_id,signed,bank_of_deposit,bank_account,people_bank_pay_sys_number,agreement_number,focus_user_remark,other_bank FROM t_w_bank WHERE main = '1' AND deleted = '0' ) t3 ON t3.use_water_unit_id = t2.id

SELECT t1.id, t1.pay_type as payType, t1.unit_code AS unitCode, t1.unit_name AS unitName, t2.unit_code_group as unitCodeGroup, t1.count_year AS countYear, t1.count_quarter AS countQuarter, t1.price, t1.year_plan AS yearPlan, t1.quarter_plan AS quarterPlan, t1.water_num1 AS waterNum1, t1.water_num2 AS waterNum2, t1.water_num2 AS waterNum3, t1.water_num_amount AS waterNumAmount, t1.exceed_water AS exceedWater, t1.pay_ratio AS payRatio, t1.multiple, t1.actual_multiple AS actualMultiple, t1.amount_receivable AS amountReceivable, t1.actual_amount AS actualAmount, t1.increase_money AS increaseMoney, t1.pay_status AS payStatus,
case t1.pay_status when '1' then 1 else 0 end as payStatusName, t1.entrusted, t1.cash_check AS cashCheck, t1.transfer_check AS transferCheck, t1.printed, t1.audit_time AS auditTime, t1.audit_person AS auditPerson, t1.invoice_num AS invoiceNum, t1.invoice_print_time AS invoicePrintTime, t2.id as useWaterUnitId, t2.responsibility_code AS responsibilityCode, t2.water_meter_code as waterMeterCode, t4.other_bank AS otherBank, t4.bank_of_deposit as bankOfDeposit, t4.bank_account as bankAccount, t1.remarks,
case #{countQuarter} when '1' then t5.one_quarter when '2' then t5.two_quarter when '3' then t5.three_quarter when '4' then t5.four_quarter ELSE '0' end as baseQuarterPlan,
case when t4.signed ='1' then '是' when t4.signed='0' then '否' ELSE '否' end as signed, case pay_type when '2' THEN '现金' when '3' THEN '转账' ELSE '' END AS payType, countDate=CAST(count_year AS VARCHAR(5))+'第'+CAST(count_quarter AS VARCHAR(5))+'季度', case pay_status when '1' THEN '1' ELSE '0' END AS isSigning FROM ( SELECT * FROM t_w_water_use_pay_info WHERE 1 = 1
and unit_name like '%${unitName}%'
and unit_code like '%${unitCode}%'
and count_year=#{countYear}
and count_quarter=#{countQuarter}
and (pay_status='1' or pay_status= '5' )
and pay_status not in ('1','2','3','5')
and actual_amount =#{actualAmount}
and edited_actual=#{editedActual}
and pay_type=#{payType}
) t1 inner join ( select id , [water_meter_code] = STUFF ( ( SELECT ',' + [water_meter_code] FROM t_w_use_water_unit_meter t WHERE t.use_water_unit_id = uu.id FOR XML PATH ( '' ) ),1,1,''), responsibility_code, unit_code_group from t_w_use_water_unit uu where deleted='0'
and unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId})
and unit_code_type=#{userType}
) t2 on t2.id=t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_bank WHERE deleted = '0' and main='1' ) t4 ON t4.use_water_unit_id = t1.use_water_unit_id LEFT JOIN ( SELECT * FROM t_w_use_water_base_plan WHERE deleted = '0' ) t5 ON t5.use_water_unit_id = t1.use_water_unit_id AND t5.plan_year = t1.count_year
where t2.water_meter_code like '%${waterMeterCode}%'
ORDER BY t1.unit_code, unit_code_group, count_quarter DESC

select t1.id, t1.unit_code as unitCode, t1.unit_name as unitName, t1.count_year as countYear, t1.count_quarter as countQuarter, t3.mobile_number as mobileNumber, t1.exceed_water as exceedWater, t1.increase_money as increaseMoney, t3.contacts as receiverName from ( select id, unit_code, unit_name, count_year, count_quarter, exceed_water, increase_money from t_w_water_use_pay_info where node_code=#{nodeCode} and pay_status != '1' and substring( unit_code, 5, 2 ) in(select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId} ) and pay_status != '5'
and count_year=#{year}
and unit_code like '%${unitCode}%'
) as t1 inner join ( select unit_code from t_w_use_water_unit where deleted='0' and unit_code_type in (select unit_type_code as unit_code_type from t_w_use_water_unit_role where person_id=#{userId}) ) as t2 on t2.unit_code =t1.unit_code inner JOIN ( SELECT unit_code, mobile_number, contacts from t_w_use_water_unit_contacts where deleted='0' and main='1' ) as t3 on t3.unit_code=t2.unit_code
