mapper.select:
SELECT ta.unitName,ta.useWaterNumber
FROM (
   SELECT top 10 t1.unit_name AS unitName,t2.number AS useWaterNumber
   FROM
   (SELECT id,industry,unit_name
   FROM  t_w_use_water_unit
   WHERE node_code = #{nodeCode}
   AND deleted='0'
   AND industry = #{id}
   )t1
   LEFT JOIN
   ( SELECT sum(COALESCE (water_number,0)) AS number,use_water_unit_id,use_year
    FROM t_w_water_use_data
   WHERE use_year =#{year}
   GROUP BY use_water_unit_id,use_year
   )t2
   ON t1.id=t2.use_water_unit_id
   )ta
 WHERE ta.useWaterNumber  <>0
 ORDER BY ta.useWaterNumber DESC
