mapper.select:
SELECT
SUM(COALESCE(january_count+february_count+march_count,0)) as firstQuarter
FROM t_w_water_month_use_data WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND use_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(april_count+may_count+june_count,0)) as secondQuarter
FROM t_w_water_month_use_data WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND use_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(july_count+august_count+september_count,0)) as thirdQuarter
FROM t_w_water_month_use_data WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND use_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(october_count+november_count+december_count,0)) as fourthQuarter
FROM t_w_water_month_use_data WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND use_year = #{planYear}

SELECT
SUM(COALESCE(first_quarter,0)) as firstQuarter
FROM t_w_use_water_plan WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND plan_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(second_quarter,0)) as secondQuarter
FROM t_w_use_water_plan WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND plan_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(third_quarter,0)) as thirdQuarter
FROM t_w_use_water_plan WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND plan_year = #{planYear}

UNION ALL
SELECT
SUM(COALESCE(fourth_quarter,0)) as fourthQuarter
FROM t_w_use_water_plan WHERE 1 = 1

  AND node_code = #{nodeCode}


  AND plan_year = #{planYear}
