mapper.select:
SELECT top ${json.pageSize} temp_row.* FROM ( select row_number () over (order by t3.status ) AS rownumber, t1.*, case when t3.status is null or t3.status ='' then '未发送' when t3.status ='0' then '发送成功' when t3.status='1' then '发送失败' when t3.status='2' then '等待回执' end as statusName from
select #{item.id} as id, #{item.unitCode} as unitCode, #{item.unitName} as unitName, #{item.countYear} as countYear, #{item.countQuarter} as countQuarter, #{item.mobileNumber} as mobileNumber, #{item.exceedWater} as exceedWater, #{item.increaseMoney} as increaseMoney, #{item.receiverName} as receiverName
t1 left join ( select id, business_id from t_sms_info as a where message_type=#{json.messageTypecode} ) as t2 on t2.business_id=t1.id left join( select a.sms_id, a.unit_code, a.phone_number, a.status from t_sms_sendInfo as a inner join ( select sms_id ,unit_code,phone_number,max(send_time) as send_time from t_sms_sendInfo as a group by sms_id ,unit_code,phone_number )as b on a.sms_id=b.sms_id and a.unit_code=b.unit_code and a.phone_number=b.phone_number and a.send_time=b.send_time ) as t3 on t3.sms_id =t2.id and t3.unit_code=t1.unitCode and t3.phone_number=t1.mobileNumber where 1=1
and 1=1
and status is null or status =''
and status=#{json.status}
) temp_row WHERE rownumber > ( (#{json.current}-1)* #{json.pageSize} );

select count(1) from
select #{item.id} as id, #{item.unitCode} as unitCode, #{item.unitName} as unitName, #{item.mobileNumber} as mobileNumber, #{item.receiverName} as receiverName
t1 left join ( select id, business_id from t_sms_info as a where message_type=#{json.messageTypecode} ) as t2 on t2.business_id=t1.id left join( select sms_id, unit_code, phone_number, status from t_sms_sendInfo WHERE 1=1
AND send_time >= #{json.startTime} AND send_time <= #{json.endTime}
AND send_time >= #{json.endTime}
) as t3 on t3.sms_id =t2.id and t3.unit_code=t1.unitCode and t3.phone_number=t1.mobileNumber where 1=1
and 1=1
and status is null or status =''
and status=#{json.status}

SELECT top ${json.pageSize} temp_row.* FROM ( select row_number () over (order by t3.status ) AS rownumber, t1.*, case when t3.status is null or t3.status ='' then '未发送' when t3.status ='0' then '发送成功' when t3.status='1' then '发送失败' when t3.status='2' then '等待回执' end as statusName from
select #{item.id} as id, #{item.unitCode} as unitCode, #{item.unitName} as unitName, #{item.mobileNumber} as mobileNumber, #{item.exceedWater} as exceedWater, #{item.receiverName} as receiverName
t1 left join ( select id, business_id from t_sms_info where message_type=#{json.messageTypecode} ) as t2 on t2.business_id=t1.id left join( select sms_id, unit_code, phone_number, status, max(send_time) as send_time from t_sms_sendInfo WHERE 1=1
AND send_time >= #{json.startTime} AND send_time <= #{json.endTime}
AND a.send_time >= #{json.endTime}
GROUP BY sms_id,unit_code,phone_number,status )t3 ON t2.id=t3.sms_id and t3.unit_code=t1.unitCode and t3.phone_number=t1.mobileNumber where 1=1
and 1=1
and t3.status is null or t3.status =''
and t3.status=#{json.status}
) temp_row WHERE rownumber > ( (#{json.current}-1)* #{json.pageSize} );

SELECT top ${json.pageSize} temp_row.* FROM ( select row_number () over (order by t3.status ) AS rownumber, t1.*, case when t3.status is null or t3.status ='' then '未发送' when t3.status ='0' then '发送成功' when t3.status='1' then '发送失败' when t3.status='2' then '等待回执' end as statusName from
select #{item.id} as id, #{item.unitCode} as unitCode, #{item.unitName} as unitName, #{item.mobileNumber} as mobileNumber, #{item.exceedWater} as exceedWater, #{item.receiverName} as receiverName
t1 left join ( select id, business_id from t_sms_info where message_type=#{json.messageTypecode} ) as t2 on t2.business_id=t1.id left join( select sms_id, unit_code, phone_number, status, max(send_time) as send_time from t_sms_sendInfo GROUP BY sms_id,unit_code,phone_number,status )t3 ON t2.id=t3.sms_id and t3.unit_code=t1.unitCode and t3.phone_number=t1.mobileNumber where 1=1
and 1=1
and t3.status is null or t3.status =''
and t3.status=#{json.status}
) temp_row WHERE rownumber > ( (#{json.current}-1)* #{json.pageSize} );
